import React from "react";
import { YMaps, Map } from "react-yandex-maps";
import { FaInstagram, FaFacebook } from "react-icons/fa";
import { Row, Col, ListGroup } from "react-bootstrap";
import { AiFillPhone } from "react-icons/ai";

const mapState = { center: [51.65516, 46.025818], zoom: 18 };

class MapBasics extends React.Component {
  state = { showMap: true };

  toggleMap() {
    const { showMap } = this.state;
    this.setState({ showMap: !showMap });
  }

  render() {
    const { showMap } = this.state;

    return (
      <div>
        <div className="p-1">
          <Row>
            <Col xs="4">
              <ListGroup variant="flush">
                <ListGroup.Item>
                  <AiFillPhone />
                  тел: 7350 1505
                </ListGroup.Item>
                <ListGroup.Item>mail: @proday_taz</ListGroup.Item>
                <ListGroup.Item>
                  <FaInstagram /> instagram/@proday_taz
                </ListGroup.Item>
                <ListGroup.Item>
                  Адрес: ул. дачная 6А пос. Зоринский, Россия
                </ListGroup.Item>
              </ListGroup>
            </Col>
            <Col xs="8">
              <YMaps>
                <div id="map-basics">
                  {showMap && (
                    <Map width="600px" height="600px" state={mapState} />
                  )}
                </div>
              </YMaps>
            </Col>
          </Row>
        </div>
      </div>
    );
  }
}

export default MapBasics;
