import React from "react";
import Basket from "./Basket";

export class Header extends Component {
  render() {
    return (
      <header className="text-white bg-dark py-3">
        <div className="container d-flex justify-content-between">
          <nav>
            <h3 className="d-flex align-items-centr">
              Продай свой таз за раз!!!
            </h3>
          </nav>
          <a
            href="/Basket"
            className="list-group-item"
            onClick={(e) => this.props.handleShowCart(e)}
          >
            {" "}
            Корзина ( {this.props.cartItems.length} )
          </a>
          {this.props.showCart ? (
            <div className="dialog-styles">
              <button
                className="dialog-close-button"
                onClick={(e) => this.props.handleShowCart(e)}
              >
                x
              </button>

              <Basket
                showCart={this.props.showCart}
                cartItems={this.props.cartItems}
                handleRemoveFromCart={this.props.handleRemoveFromCart}
              />
            </div>
          ) : null}
        </div>
      </header>
    );
  }
}
Header.propTypes = {
  showCart: PropTypes.bool.isRequired,
  cartItems: PropTypes.array.isRequired,
  handleShowCart: PropTypes.func.isRequired,
  handleRemoveFromCart: PropTypes.func.isRequired,
};

export default Header;
