import React, { useEffect, useState } from "react";

function Basket(props) {
  const [catalog, onAdd, onRemove, setCatalog] = useState([]);
  useEffect(() => {
    fetch("/catalog")
      .then((res) => res.json())
      .then((data) => setCatalog(data));
  }, []);

  return (
    <aside className="block col-1">
      <h2>Cart Items</h2>
      <div>{catalog.length === 0 && <div>Cart Is Items</div>}</div>
      {catalog.map((el) => (
        <div key={el.id} className="row">
          <div className="col-2">{el.name}</div>
          <div className="block col-2">
            <button onClick={() => onAdd(el)} className="add">
              +
            </button>
            <button onClick={() => onRemove(el)} className="remove">
              +
            </button>
          </div>
          <div className="col-2 text-right">
            {el.qty} x ${el.price.toFixed(2)}
          </div>
        </div>
      ))}
    </aside>
  );
}

export default Basket;
