import React from "react";
import { YMaps, Map } from "react-yandex-maps";
import { FaTelegram, FaInstagram, FaFacebook } from "react-icons/fa";
import { Row, Col, Toast, ToastBody, ToastHeader } from "react-bootstrap";

const mapState = { center: [51.65516, 46.025818], zoom: 18 };

class MapBasics extends React.Component {
  state = { showMap: true };

  toggleMap() {
    const { showMap } = this.state;
    this.setState({ showMap: !showMap });
  }

  render() {
    const { showMap } = this.state;

    return (
      <div>
        <div className="p-1">
          <Col xs="4">
            <a href="/Contacts" class="list-group-item">
              Тел отдела продаж: 7350 1505
            </a>
          </Col>
          <Row>
            <Col xs="12">
              <YMaps>
                <div id="map-basics">
                  {showMap && (
                    <Map width="600px" height="600px" state={mapState} />
                  )}
                </div>
              </YMaps>
            </Col>
          </Row>
        </div>
      </div>
    );
  }
}

export default MapBasics;
