import React, { useEffect, useState } from "react";
import { Container, Row, Col, Card, Button } from "react-bootstrap";

function Catalog(props) {
  const [catalog, setCatalog] = useState([]);
  useEffect(() => {
    fetch("/catalog")
      .then((res) => res.json())
      .then((data) => setCatalog(data));
  }, []);

  const setCount = props.setCount;

  return (
    <Container>
      <Row>
        <Col xs={9}>
          <Row>
            {catalog.map((el) => (
              <Col sm={12} md={6} lg={4} xl={4} className="d-flex">
                <Card className="mb-3">
                  <Card.Img variant="top" src={el.image} className="auto-img" />
                  <Card.Body className="auto-body">
                    <Card.Title>{el.name}</Card.Title>
                    <Card.Text className="d-flex justify-content-between align-items-centr">
                      {el.description}
                    </Card.Text>
                    <Button
                      onClick={() => setCount()}
                      className="justify-content-between"
                      variant="dark"
                    >
                      Купить <span> {el.price} РУБ.</span>
                    </Button>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        </Col>
      </Row>
    </Container>
  );
}

export default Catalog;
