import React, { useEffect, useState } from "react";
import { Container, Row, Col, Card } from "react-bootstrap";

function Catalog() {
  const [setBasket] = useState([]);
  useEffect(() => {
    fetch("/catalog")
      .then((res) => res.json(el))
      .then((data) => setBasket(data));
  }, []);

  <Container>
    <Row>
      <Col xs={9}>
        <Row>
          {el.map((el) => (
            <Col sm={12} md={6} lg={4} xl={4} className="d-flex">
              <Card className="mb-3">
                <Card.Img variant="top" src={el.image} className="auto-img" />
                <Card.Body className="auto-body">
                  <Card.Title>{el.name}</Card.Title>
                  <Card.Text className="d-flex justify-content-between align-items-centr">
                    {el.description}
                  </Card.Text>
                  <Button
                    className="justify-content-between"
                    variant="dark"
                    onClick={() => onAdd(product)}
                  >
                    Купить <span> {el.price} РУБ.</span>
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Col>
    </Row>
  </Container>;
}

export default Catalog;
