import React, { Component } from "react";
import PropTypes from "prop-types";

export class Filter extends Component {
  render() {
    return (
      <div className="col-md-12">
        <div className="row mt-4">{this.props.count}</div>
        <div className="row mt-4">
          <label>
            Сортировать
            <select
              className="form-control mt-1"
              value={this.props.sort}
              onChange={this.props.handleChangeSort}
            >
              <option value="">Без сортировки</option>
              <option value="lowest">По возрастанию</option>
              <option value="highest">По убыванию</option>
            </select>
          </label>
        </div>
        <div className="row mt-4">
          <label>
            Фильтр Техники
            <select
              className="form-control mt-1"
              value={this.props.type}
              onChange={this.props.handleChangeType}
            >
              <option value="">Вся техника</option>
              <option value="tool">Мотоциклы</option>
              <option value="food">Автомобили</option>
              <option value="furniture">Катера</option>
            </select>
          </label>
        </div>
      </div>
    );
  }
}

Filter.propTypes = {
  type: PropTypes.string.isRequired,
  sort: PropTypes.string.isRequired,
  handleChangeSort: PropTypes.func.isRequired,
  handleChangeType: PropTypes.func.isRequired,
};

export default Filter;
