import React from "react";
import { YMaps, Map } from "react-yandex-maps";
import { FaTelegram, FaInstagram, FaFacebook } from "react-icons/fa";
import { Row, Col, Toast, ToastBody, ToastHeader } from "react-bootstrap";

const mapState = { center: [51.65516, 46.025818], zoom: 18 };

class MapBasics extends React.Component {
  state = { showMap: true };

  toggleMap() {
    const { showMap } = this.state;
    this.setState({ showMap: !showMap });
  }

  render() {
    const { showMap } = this.state;

    return (
      <div>
        <div className="p-1">
          <Row>
            <Col xs="4">
              <div
                class="card text-white bg-dark mb-3"
                style="max-width: 18rem;"
              >
                <div class="card-header">Header</div>
                <div class="card-body">
                  <h5 class="card-title">Dark card title</h5>
                  <p class="card-text">
                    Some quick example text to build on the card title and make
                    up the bulk of the card's content.
                  </p>
                </div>
              </div>
            </Col>
            <Col xs="8">
              <YMaps>
                <div id="map-basics">
                  {showMap && (
                    <Map width="600px" height="600px" state={mapState} />
                  )}
                </div>
              </YMaps>
            </Col>
          </Row>
        </div>
      </div>
    );
  }
}

export default MapBasics;
