import React from "react";
import { YMaps, Map } from "react-yandex-maps";
import { FaTelegram, FaInstagram, FaFacebook } from "react-icons/fa";
import {
  Row,
  Col,
  Toast,
  ToastBody,
  ToastHeader,
  ListGroup,
} from "react-bootstrap";

const mapState = { center: [51.65516, 46.025818], zoom: 18 };

class MapBasics extends React.Component {
  state = { showMap: true };

  toggleMap() {
    const { showMap } = this.state;
    this.setState({ showMap: !showMap });
  }

  render() {
    const { showMap } = this.state;

    return (
      <div>
        <div className="p-1">
          <Row>
            <Col xs="4">
              <ListGroup variant="flush">
                <ListGroup.Item>Cras justo odio</ListGroup.Item>
                <ListGroup.Item>Dapibus ac facilisis in</ListGroup.Item>
                <ListGroup.Item>Morbi leo risus</ListGroup.Item>
                <ListGroup.Item>Porta ac consectetur ac</ListGroup.Item>
              </ListGroup>
              <a href="/Contacts" class="list-group-item">
                Адрес: ул. дачная 6А пос. Зоринский, Россия
              </a>
            </Col>
            <Col xs="8">
              <YMaps>
                <div id="map-basics">
                  {showMap && (
                    <Map width="600px" height="600px" state={mapState} />
                  )}
                </div>
              </YMaps>
            </Col>
          </Row>
        </div>
      </div>
    );
  }
}

export default MapBasics;
