import React from "react";

const ShopList = ({ catalog, addPhoneInCart }) => {
  return (
    <section className="shop-list">
      <ul className="shop-list__list">
        {
          // Перебираем массив объектов, передавая каждый в компонент CartListItem.
          // В li передаём key, равный id и говорим, что при клике на элемент вызывается переданная функция, принимая id элемента
          catalog.map((phone) => {
            const { id } = phone;

            return (
              <li
                key={id.toString()}
                className="shop-list__item"
                onClick={() => addPhoneInCart(id)}
              >
                <ShopListItem phone={phone} />
              </li>
            );
          })
        }
      </ul>
    </section>
  );
};

const Basket = ({ props }) => {
  const { name, description, price, url } = props;

  return (
    <div className="shop-list-item">
      <div className="shop-list-item__header">
        <div className="shop-list-item__image">
          <img
            src={el.image}
            alt="Картинка телефона"
            className="shop-list-item__url"
          />
        </div>
        <h3 className="shop-list-item__name">{el.name}</h3>
      </div>
      <div className="shop-list-item__content">
        <p className="shop-list-item__description">{el.description}</p>
        <span className="shop-list-item__price">Цена: {el.price}</span>
      </div>
      <button className="button">Купить</button>
    </div>
  );
};

export default Basket;
